urlset:
{% for item in items %}
    {% if item.type == "urls" %}
        {% for url in item.urls %}

                {% for key, value in url.items %}
                    {% if key == "loc" %}
                        {{ value }}
                    {% elif key == "lastmod" %}
                        {{ value }}
                    {% elif key == "changefreq" %}
                        {{ value }}
                    {% elif key == "images" %}
                        {% for image in value %}

                                {% for key1, value1 in image.items %}
                                    {% if key1 == "loc" %}
                                        {{ value1 }}
                                    {% endif %}
                                {% endfor %}

                        {% endfor %}
                    {% endif %}
                {% endfor %}

        {% endfor %}
    {% elif item.type == "sitemaps" %}

            {% for sitemap in item.sitemaps %}

                    {% for key, value in sitemap.items %}
                        {% if key == "loc" %}
                            {{ value }}
                        {% elif key == "lastmod" %}
                            {{ value }}
                        {% elif key == "changefreq" %}
                            {{ value }}
                        {% elif key == "images" %}
                            {% for image in value %}

                                    {% for key1, value1 in image.items %}
                                        {% if key1 == "loc" %}
                                            {{ value1 }}
                                        {% endif %}
                                    {% endfor %}

                            {% endfor %}
                        {% endif %}
                    {% endfor %}

            {% endfor %}

    {% elif item.type == "images" %}
        {% for image in item.images %}

                {% for key, value in image.items %}
                    {% if key == "loc" %}
                        {{ value }}
                    {% endif %}
                {% endfor %}

        {% endfor %}
    {% endif %}
{% endfor %}
